mapper.update:
UPDATE qna
SET
    qna_context = #{qna_context}
WHERE qna_no = #{qna_no}

mapper.select:
SELECT q.qna_no, q.m_no, q.qna_title, q.qna_regdate,q.qna_originno, q.qna_groupOrd, q.privateck , m.m_id, m.m_name FROM qna q inner join member m on q.m_no = m.m_no WHERE 1=1
AND q.qna_context like concat('%', ${searchKeyword}, '%')
ORDER BY q.qna_originno DESC, q.qna_groupOrd ASC LIMIT #{limitBegin}, #{articleCount}

SELECT count(1) as cnt FROM qna
where qna_no = #{qna_no}
and qna_passwd = password(#{qna_passwd});
