    p.p_name = #{productVO.p_name}, p.p_price=#{productVO.p_price}, p.p_brand=#{productVO.p_brand}, p.p_spec=#{productVO.p_spec}, cp.category_no = #{category_no}


WHERE p.p_id = #{productVO.p_id} and cp.p_id = #{productVO.p_id}

